from shutit_module import ShutItModule

class test13(ShutItModule):


	def build(self, shutit):
		b = '''a
b
'''
		# Test a long line being sent
		shutit.send('''cat > /dev/null << END
''' + b + '''
END''')
		a = '''a
b
c
d
e
'''
		# Test a long line being sent
		shutit.send('''cat > /dev/null << END
''' + a + '''
END
''')
		shutit.send_file('/tmp/a',a)

		res1 = shutit.send_and_get_output('md5sum /tmp/a')
		shutit.insert_text(r'''b
c''','/tmp/a','a',line_oriented=False)
		res2 = shutit.send_and_get_output('md5sum /tmp/a')
		if res1 != res2:
			shutit.fail('file was altered') # pragma: no cover

		res1 = shutit.send_and_get_output('md5sum /tmp/a')
		shutit.insert_text('''c
c''','/tmp/a','a',line_oriented=False)
		res2 = shutit.send_and_get_output('md5sum /tmp/a')
		if res1 == res2:
			shutit.fail('file not altered') # pragma: no cover

		return True

def module():
	return test13(
		'shutit.tk.test13.test13', 782914092.0013987,
		description='',
		maintainer='',
		depends=['shutit.tk.setup']
	)

